import React, {Component} from 'react';
import "./Contact.css"
//import { ReactComponent } from '*.svg';



class Contact extends React.Component{ // convertir en Class
                    // creer un contructor et initialiser un state {onlin : false}
                    // au  clic sur le status modifie le state du online 
                    // et change le css + le texte selon un rendu conditionnel
  constructor(props){
    super(props)
    this.users = [
        {
          name: 'Robert Reyes',
          avatar: 'https://randomuser.me/api/portraits/men/28.jpg',
          online: false
        },
        {
          name: 'Nellie Caldwell',
          avatar: 'https://randomuser.me/api/portraits/women/17.jpg',
          online: true
        },
        {
          name: 'Vernon Mason',
          avatar: 'https://randomuser.me/api/portraits/men/84.jpg',
          online: true
        },
        {
          name: 'Erica Hunt',
          avatar: 'https://randomuser.me/api/portraits/women/87.jpg',
          online: false
        },
        {
          name: 'Juanita Phillips',
          avatar: 'https://randomuser.me/api/portraits/women/55.jpg',
          online: true
        }
      ];
    // this.onOff = this.users.online
    this.state = {online: true}
  }

  test = () => console.log(this.users.online)
  // isOnline = () => this.users.map( event => console.log("test") );
  isOnline = event => this.state ? this.setState({online: false}) : this.setState({online: true});


  render(){

    return (
    <div className="Contact">

          <div className="status">
            <div className="status-text">
              {this.users.map ( index => 
                <ul >

                  <li className="name">
                    {index.name}
                  </li>

                  <img className="avatar" src={index.avatar} />

                  <li className={ index.online ? "status-online" : "status-offline"}>
                  </li>

                  <p>
                    <button onClick={this.isOnline}>Change status</button>
                    {index.online ? "online" : "offline"}
                  </p>

                </ul>)
              }
            </div>
          </div>
    </div>)
  }
}

export default Contact;
