import React from 'react';
import './QuoteForm.css';

class QuoteForm extends React.Component {

    constructor(props){
        super(props)
        this.state = {character: "" }; 
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event){
        this.setState({character: event.target.value})
    }

    handleSubmit(event){
        const addParagraph = event => <p>{this.state.character}</p>
        event.preventDefaultListener(addParagraph);        
    }

    render() {
      return (
        <form className="QuoteForm" onSubmit={this.handleSubmit}>

            <label htmlFor="character">Character:</label>

            <input id="character" name="character" type="text" value={this.state.character}
                /* onChange={(event) => {
                const input = event.target;
                console.log('NAME:', input.name, 'VALUE:', input.value);
                }} */

             onChange={this.handleChange}

                // onChange={event => this.setState({character: event.target.value})}
            />
            <input type="submit" value="Submit"/>
            <p>Here comes e new challenger {this.state.character}</p>

            {this.handleSubmit}

        </form>
      );
    }
  }



  export default QuoteForm;
